import { 
  EsButton, 
  EsTypography, 
  EsContainer, 
  EsDivider,
  EsToast,
  EsCard,
  EsSelectInput,
  EsCheckbox
} from '@esentire/fabric'

// Default button configuration
const defaultButtonConfig = reactive({
  variant: 'primary',
  size: 'md',
  outline: false,
  disabled: false,
  text: 'Click Me!'
})

// Icon button configuration
const iconButtonConfig = reactive({
  variant: 'primary',
  size: 'md',
  icon: 'file-plus',
  iconAfter: '',
  circular: false,
  outline: false,
  text: 'Action Button'
})

// Toast state
const showToast = ref(false)
const toastMessage = ref('')
const toastVariant = ref('success')

// Event handlers
const handleDefaultButtonClick = () => {
  toastMessage.value = 'Default button clicked!'
  toastVariant.value = 'success'
  showToast.value = true

  setTimeout(() => {
    showToast.value = false
  }, 3000)
}

const handleIconButtonClick = () => {
  toastMessage.value = 'Icon button clicked!'
  toastVariant.value = 'info'
  showToast.value = true

  setTimeout(() => {
    showToast.value = false
  }, 3000)
}",
