                <div class="button-row">
                  <EsButton icon="play" variant="success">Start</EsButton>
                  <EsButton icon="pause" variant="warning">Pause</EsButton>
                  <EsButton icon="stop" variant="danger">Stop</EsButton>
                </div>
              </div>
            </div>
          </div>

          <!-- Interactive Demo -->
          <div class="example-group">
            <EsTypography variant="h4" class="example-title">Interactive Demo</EsTypography>
            <div class="interactive-demo">
              <EsButton 
                :variant="iconButtonConfig.variant"
                :size="iconButtonConfig.size"
                :icon="iconButtonConfig.icon"
                :icon-after="iconButtonConfig.iconAfter"
                :circular="iconButtonConfig.circular"
                :outline="iconButtonConfig.outline"
                @click="handleIconButtonClick"
              >
                {{ iconButtonConfig.text }}
              </EsButton>

              <div class="demo-controls">
                <div class="control-group">
                  <label>Icon:</label>
                  <select v-model="iconButtonConfig.icon">
                    <option value="">None</option>
                    <option value="file-plus">file-plus</option>
                    <option value="edit">edit</option>
                    <option value="trash">trash</option>
                    <option value="download">download</option>
                    <option value="search">search</option>
                  </select>
                </div>

                <div class="control-group">
                  <label>Icon After:</label>
                  <select v-model="iconButtonConfig.iconAfter">
                    <option value="">None</option>
                    <option value="arrow-right">arrow-right</option>
                    <option value="external-link">external-link</option>
                    <option value="chevron-down">chevron-down</option>
                  </select>
                </div>

                <div class="control-group">
                  <label>
                    <input 
                      type="checkbox" 
                      v-model="iconButtonConfig.circular"
                    > Circular
                  </label>
                </div>

                <div class="control-group">
                  <label>
                    <input 
                      type="checkbox" 
                      v-model="iconButtonConfig.outline"
                    > Outline
                  </label>
                </div>
              </div>
            </div>
          </div>
        </div>

        <!-- Code Example -->
        <div class="code-example">
          <EsTypography variant="h4" class="example-title">Code Examples</EsTypography>
          <pre><code><!-- Icon with text -->
&lt;EsButton icon="file-plus" @click="createFile"&gt;
  Create New File
&lt;/EsButton&gt;

<!-- Icon only (circular) -->
&lt;EsButton 
  icon="edit" 
  :circular="true" 
  title="Edit"
  @click="editItem"
&gt;&lt;/EsButton&gt;

<!-- Icon before and after -->
&lt;EsButton 
  icon="download" 
  icon-after="external-link"
  @click="downloadFile"
&gt;
  Download
&lt;/EsButton&gt;</code></pre>
        </div>
      </section>

      <!-- Usage Guidelines -->
      <section class="guidelines-section">
        <EsTypography variant="h2" class="section-title">
          Usage Guidelines
        </EsTypography>

        <div class="guidelines-grid">
          <div class="guideline-card">
            <EsTypography variant="h4">Default Button Best Practices</EsTypography>
            <ul>
              <li>Use primary variant for main actions (save, submit, confirm)</li>
              <li>Use secondary for less important actions</li>
              <li>Keep button text concise and action-oriented</li>
              <li>Use appropriate sizes based on context</li>
              <li>Provide clear visual hierarchy with variants</li>
            </ul>
          </div>

          <div class="guideline-card">
            <EsTypography variant="h4">Icon Button Best Practices</EsTypography>
            <ul>
              <li>Use recognizable icons that match user expectations</li>
              <li>Provide tooltips for icon-only buttons</li>
              <li>Use circular buttons for single actions</li>
              <li>Combine icons with text for clarity when needed</li>
              <li>Maintain consistent icon sizing across the interface</li>
            </ul>
          </div>
        </div>
      </section>

      <!-- Toast for Demo Feedback -->
      <EsToast 
        v-if="showToast"
        :variant="toastVariant"
        @close="showToast = false"
      >
        {{ toastMessage }}
      </EsToast>
    </EsContainer>
  </div>
</template>

<script setup lang="ts">
import { ref, reactive } from 'vue'
